      flash[:error]="product not updated"      
      render "edit"
    end               
  end

  def show    
    @product = Product.where(:user_id=>@user.id,:id=>params[:id]).first #@product=@user.products.find(params[:id])
    @photos = @product.photos if @product
    # render :text=>@photos.inspect and return false
  end

  def destroy
    @product = Product.where(:user_id=>@user.id,:id=>params[:id]).first  #@product=@user.products.find(params[:id])
    if @product.destroy
      flash[:notice]="product deleted scuccessfully"
    else
      flahs[:error]="product not deleted"
    end
    redirect_to products_url
  end
end
